mapper.select:
SELECT * from post
where postNo=#{postNo}
order by createdDate desc, postNo desc

SELECT * from post
where type='community'
LIMIT #{limit}, 10;

mapper.update:
update post
set title   = #{title}
  , content = #{content}
  , modDate = now()
where postNo = #{postNo}
  and userId = #{userId}
