mapper.select:
select count(*) from product p, category c, category c1 where c.pcategory_no = c1.category_no and p.productCategory = c.NAME
and c1.name = #{category}
AND p.productName like concat('%',#{keyword},'%')

select A.productNo ,productCategory ,productName ,productPrice ,productStock ,productImgPath from (select p.productNo, p.productCategory, p.productName, p.productPrice, p.productStock, p.productImgPath from product p, category c, category c1 where c.pcategory_no = c1.category_no and p.productCategory = c.NAME
and c1.name = #{category}
) A LEFT JOIN (select productNo, avg(score) score from review group by productNo) B ON A.productNo=B.productNo where productName like concat('%',#{keyword},'%')
LIMIT #{offset}, 9;

mapper.update:
update product
set productName  = #{productName},
    productPrice = #{productPrice},
    productStock = #{productStock},
    productImgPath = #{productImgPath}
where productNo = #{productNo};
