mapper.select:
select *
from review
where orderNo = #{orderNo}
  and goodsNo = #{goodsNo}

select reviewNo, filePath as uploadPath
from reviewImg
where goodsNo = #{goodsNo}
  and reviewNo = #{reviewNo};

select reviewNo, filePath as uploadPath
from reviewImg
  where reviewNo = '${reviewNo}';

mapper.update:
UPDATE review
SET modDate = now(),
    score = #{score},
    content = #{content}
where reviewNo = #{reviewNo}

mapper.delete:
UPDATE review
SET expiredDate = now()
where reviewNo = ${reviewNo}
